
import { useEffect, useRef, useState } from 'react'
import { useApp } from '../state/AppState'
import { CameraIcon, PencilIcon } from '../assets/icons'

export function HomeView() {
  const { tasks } = useApp()
  const [name, setName] = useState<string>('Diễm Ngọc (cute :>)')
  const [avatar, setAvatar] = useState<string | null>('data:image/jpeg;base64,')
  const [editingName, setEditingName] = useState(false)
  const fileRef = useRef<HTMLInputElement>(null)

  useEffect(() => {
    try {
      const raw = localStorage.getItem('nv_profile')
      if (raw) {
        const p = JSON.parse(raw)
        if (p.name) setName(p.name)
        if (p.avatar) setAvatar(p.avatar)
        else setAvatar('/dn.jpg')
      } else {
        setAvatar('/dn.jpg')
      }
    } catch {
      setAvatar('/dn.jpg')
    }
  }, [])

  const saveProfile = (n = name, a = avatar) => {
    localStorage.setItem('nv_profile', JSON.stringify({ name: n, avatar: a }))
  }

  const onPickAvatar = () => fileRef.current?.click()
  const onFile = (e: React.ChangeEvent<HTMLInputElement>) => {
    const f = e.target.files?.[0]
    if (!f) return
    const reader = new FileReader()
    reader.onload = () => {
      const data = String(reader.result || '')
      setAvatar(data)
      saveProfile(name, data)
    }
    reader.readAsDataURL(f)
  }

  const initials = name
    .split(/\s+/)
    .filter(Boolean)
    .slice(0, 2)
    .map((s) => s[0]?.toUpperCase())
    .join('') || 'U'

  const friendlyName = name.trim().split(/\s+/)[0] || 'friend'
  const QUOTE = 'Tr\u00ean b\u01b0\u1edbc \u0111\u01b0\u1eddng th\u00e0nh c\u00f4ng kh\u00f4ng c\u00f3 d\u1ea5u ch\u00e2n c\u1ee7a k\u1ebb l\u01b0\u1eddi bi\u1ebfng.'
  const quoteLength = QUOTE.length
  const [typedCount, setTypedCount] = useState(0)
  const [phase, setPhase] = useState<'typing' | 'pause' | 'deleting'>('typing')

  useEffect(() => {
    let timeout: ReturnType<typeof setTimeout> | undefined
    if (phase === 'typing') {
      if (typedCount < quoteLength) {
        timeout = setTimeout(() => setTypedCount((v) => v + 1), 90)
      } else {
        timeout = setTimeout(() => setPhase('pause'), 1800)
      }
    } else if (phase === 'pause') {
      timeout = setTimeout(() => setPhase('deleting'), 1600)
    } else {
      if (typedCount > 0) {
        timeout = setTimeout(() => setTypedCount((v) => v - 1), 45)
      } else {
        timeout = setTimeout(() => setPhase('typing'), 800)
      }
    }
    return () => { if (timeout) clearTimeout(timeout) }
  }, [phase, typedCount, quoteLength])

  const displayQuote = QUOTE.slice(0, typedCount)

  return (
    <section>
      <div className="home-hero">
        <div className="home-avatar" onClick={onPickAvatar} title="Change avatar">
          {avatar ? (
            <img className="avatar-img" src={avatar} alt="avatar" />
          ) : (
            <div className="avatar-fallback">{initials}</div>
          )}
          <div className="avatar-edit"><CameraIcon /></div>
          <input ref={fileRef} type="file" accept="image/*" style={{ display: 'none' }} onChange={onFile} />
        </div>

        <div className="home-name-row">
          {editingName ? (
            <input
              className="text-input big"
              style={{ textAlign: 'center' }}
              value={name}
              onChange={(e) => setName(e.target.value)}
              onBlur={() => {
                setEditingName(false)
                saveProfile()
              }}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  ;(e.currentTarget as HTMLInputElement).blur()
                }
              }}
              autoFocus
            />
          ) : (
            <div className="home-name" onClick={() => setEditingName(true)}>
              Hello, {name}
              <button className="icon-btn soft" aria-label="Edit name" onClick={() => setEditingName(true)}>
                <PencilIcon />
              </button>
            </div>
          )}
        </div>

        <div className="home-charm">
          <img src="/cats/hug-cat.gif" alt="Cat offering a hug" />
          <div>
            <strong>Cozy reminder</strong>
            <p>Take a slow breath, sip your cocoa, then conquer one tiny win at a time.</p>
          </div>
        </div>

        <div className="home-cat-strip">
          <img src="/cats/kitty-laptop.gif" alt="Cat working on a laptop" />
          <div className="cat-bubble">We can do this together, {friendlyName}! Let's make today magical.</div>
          <img src="/cats/bounce-cat.gif" alt="A cheering kitty" />
        </div>

        <div className="home-slogan">Keep planting tiny seeds of effort every day.</div>

        <div className="home-slogan-new" data-phase={phase} aria-label={QUOTE}>
          <span>{displayQuote}</span>
        </div>
        <div className="home-stats">
          <div className="stat-card">
            <div className="stat-num">{tasks.filter((t) => !t.completed).length}</div>
            <div className="stat-label">Tasks Pending</div>
          </div>
          <div className="stat-card">
            <div className="stat-num">{tasks.filter((t) => t.completed).length}</div>
            <div className="stat-label">Tasks Completed</div>
          </div>
        </div>
      </div>
    </section>
  )
}

// (home hero only) no task sorting needed here
